#!/usr/bin/perl

die("You have not entered the arguments\n") if (@ARGV == 0);
foreach $filename (@ARGV) {
    if (-e $filename) {
        print("File $filename exists\n");
        if (-B $filename) {
            print("Binary file\n");
        }
        else {
            print("Not a binary file\n");
        }
    }
    else {
        print("File $filename does not exist\n");
    }
}

#!/usr/bin/perl

die("You have not entered the arguments\n") if (@ARGV == 0);
foreach $arg (@ARGV) {
    $arg = tr/a-z/A-Z/;
    printf("%s\n", $arg);
}

#!/usr/bin/perl
foreach $num (@ARGV) {
    $original_no = $num;
    $sum = 0;
    until ($num == 0) {
        $digit = $num % 10;
        $sum += $digit;
        $num = int($num / 10);
    }
    print("sum of digits of $original_no is $sum");
}
